package es.Daniel_Gil.Arrays;


public class ARRAY_Bucle_For_Each {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] valores = {15, 7, 1500, 9, -5};
		
		/* Vamos a crear un bucle For Each
		 * Este tipo de bucle es el m√°s parecido
		 * al bucle for de python. No hay ni inicio
		 * ni final, ni crecimiento o decrecimiento.
		 * Simplemente repasa cada valor del array y lo imprime.
		 */
		
		for(int elemento: valores) {
			System.out.println(elemento);
		}
		
		
		
		

	}

}
